mapper.update:
UPDATE employee
SET 
    PhoneNumber = #{phoneNumber},
    Email = #{email},
    ProfileImage = #{profileImage}
WHERE EmployeeID = #{employeeId}

UPDATE useraccounts
SET Password = #{newPassword}
WHERE EmployeeID = #{employeeId}

mapper.select:
SELECT COUNT(*) > 0
FROM useraccounts
WHERE EmployeeID = #{employeeId}

SELECT Password
FROM useraccounts
WHERE EmployeeID = #{employeeId}
